#include <bits/stdc++.h>
const int N=1E5;char s[N];int p[N],q,Q,z,r;bool l(int f,int e){r=memcmp(s+f,s+e,z-std::max(f,e));return r?r<0:f>e;}int main(){while(scanf("%[^\n]%n",s,&z)!=-1){scanf("%d",&q);std::iota(p,p+N,0);std::sort(p,p+z,l);while(q--)scanf("%d",&Q),printf("%d ",p[Q]);printf("\n");getchar();}}